import "./Post.css";
import { Avatar } from '@material-ui/core';
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';

function Post({
    displayName,
    username,
    verified,
    text,
    image, 
    avatar,
}){
    return (
        <div className="post">
            <div className="post__avatar">
                <Avatar src="./img/earth.jpg" />
            </div>
            <div className="post__body">
                <div className="post__header">
                    <div className="post__headerText">
                        <h3>Owen Osagiede {"  "} <span><VerifiedUserIcon className="post__badge"/></span></h3>
                    </div>
                    <div className="post__headerDesription">
                        <p>I challenge you to build a Twitter clone</p>
                    </div>
                </div>
                <img src="https://i.gifer.com/UTNj.gif" alt=""/>
            </div>
        </div>
    );
}

export default Post;
