import React, { useState, useEffect } from 'react'
import TweetBox from './TweetBox';
import Post from './Post'
import './Feed.css'
import db from './firebase';

function Feed(){
    const [posts, setPosts] = useState ([]);

    useEffect (() => {
        db.collection('posts').onSnapshot(snapshot => (
            setPosts(snapshot.docs.map(doc => doc.data()))
        ))
    }, []);

    return(
        <div className="feed">
            {/* Header */}
            <div className="feed__header">
                <h2> Home </h2>
            </div> 

            {/* TweetBox */}
            <TweetBox /> 

            {/* Post */}
            <Post displayName="Owen Osagiede"
            username="owenosa" 
            verified={true}
            text="Its working"
            avatar = {'./img/earth.jpg'}
            image = "https://i.gifer.com/fxVE.gif"
            />
            {/* Post */}
            <Post />
            {/* Post */}
            <Post />
            {/* Post */}
            <Post />
            {/* Post */}
            <Post />
        </div>
    )
}

export default Feed;
