import React from 'react';
import "./Post.css";
import { Avatar } from '@material-ui/core';
import VerifiedUserIcon from '@material-ui/icons/VerifiedUser';
import ChatBubbleOutlineIcon from '@material-ui/icons/ChatBubbleOutline';
import RepeatIcon from '@material-ui/icons/Repeat';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import PublishIcon from '@material-ui/icons/Publish';

function Post({
    displayName,
    username,
    verified,
    text,
    image, 
    avatar,
}){
    return (
        <div className="post">
            <div className="post__avatar">
                <Avatar src="./img/earth.jpg" />
            </div>
            <div className="post__body">
                <div className="post__header">
                    <div className="post__headerText">
                        <h3>Owen Osagiede <VerifiedUserIcon className="post__badge"/> <span className="post__headerSpecial">{" @owenosa"}</span></h3>
                    </div>
                    <div className="post__headerDesription">
                        <p>I challenge you to build a Twitter clone</p>
                    </div>
                </div>
                <img src="http://gph.is/28PfkrF" alt=""/> 
                <div className="post__footer">
                    <ChatBubbleOutlineIcon fontSize= "small" />
                    <RepeatIcon fontSize= "small" />
                    <FavoriteBorderIcon fontSize= "small" />
                    <PublishIcon fontSize= "small" />
                </div>
            </div>
        </div>
    );
}

export default Post;
